from mcpi.minecraft import Minecraftimport mcpi.block as blockmc=Minecraft.create("47.100.46.95",4783)entityId= mc.getPlayerEntityId("cjy")pos=mc.entity.getTilePos(entityId)import timeimport turingimport threadingimport facedef where(x,y,z):    if 82<=x<=84 and -1<=y<=6 and 92<=z<=95:        return 1    #导购    elif 57<=x<=59 and -1<=y<=6 and 90<=z<=96:        return 2    #收银    elif 69<=x<=70 and -1<=y<=6 and z==82:        return 3    #入口    elif 72<=x<=73 and -1<=y<=6 and z==84:        return 4    #出口while True:    time.sleep(0.5)    pos=mc.entity.getTilePos(entityId)    a=where(pos.x,pos.y,pos.z)    if a == 1:        print("111")        threads = []  # 用来存放执行read函数线程的列表        t = threading.Thread(target=turing.chat) # 执行的函数如果需要传递参数，threading.Thread(target=函数名,args=(参数，逗号隔开))        threads.append(t)        t1 = threading.Thread(target=turing.follow) # 执行的函数如果需要传递参数，threading.Thread(target=函数名,args=(参数，逗号隔开)        threads.append(t1)                if __name__=='__main__':            for t in threads:                t.start()            for t in threads:                t.join()    elif a == 2:        import 收银系统_终        print("222")    elif a == 3:        face.face_in()        print("333")    elif a == 4:        face.face_out()        print("333")    else:        print("000")